insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_AUTOSAVE', '0', 'Автосохранение', 'Выберите этот пункт, если хотите, чтобы вся база данных сохранялась раз в сутки. Дампы старше недели автоматически удаляются.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_DEFAULT_TEMPLATE', 'default', 'Шаблон пользовательской части', 'Выберите шаблон(дизайн) для пользовательской части магазина.', 'settingCONF_DEFAULT_TEMPLATE()', 27);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_CART_METHOD', '0', 'Метод работы корзины товаров', '"Стандартный" - после выбора товара, покупатель отправляется в корзину. "В новом окне" - корзина открывается в новом окне, покупатель продолжает выбор товаров. "AjaxCart" - корзина не открывается, покупатель продолжает выбор товаров.', 'settingCONF_SELECT_CART_METHOD()', 27);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_ALLOW_COMPARISON_FOR_SIMPLE_SEARCH', '1', 'Разрешить сравнение товаров в результатах простого поиска', 'Включите, чтобы разрешить покупателям сравнивать товары при простом поиске (по имени и описанию)', 'setting_CHECK_BOX(', 26 );
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_ENABLE_CONFIRMATION_CODE', '0', 'Проверка ввода случайного кода, изображенного на графическом рисунке', 'Данная функция повышает защищенность форм регистрации и быстрого оформления заказа от автоматизированного ввода данных (например, роботами)', 'setting_CHECK_BOX(', 26 );
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_EXACT_PRODUCT_BALANCE', '0', 'Показывать пользователям точный остаток товаров на складе', 'Если опция включена, покупателям будет показано кол-во товара на складе (например, "На складе: 34"). Если опция выключена, то отображается только факт наличия или отсутствия товара (например, "На складе: да")', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_USE_GZIP', '1', 'Потоковое сжатие (gzip)', 'Если данная опция включена, вся информация выдаваемая сервером будет проходить потоковое сжатие. Опция значительно ускоряет открытие страниц магазина и существенно экономит траффик.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_DEFAULT_SORT_ORDER', 'sort_order, name', 'Сортировка по умолчанию', 'Выберите, в каком порядке по умолчанию будут сортироваться товары в категориях и результатах поиска.', 'settingCONF_DEFAULT_SORT_ORDER()', 39);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_DISPLAY_INFO', '0', 'Показывать отладочную информацию', 'Опция позволяет отображать отладочную информацию для программистов и дизайнеров в режиме администратора в пользовательской части магазина на специальной панели в верхней части страницы.', 'setting_CHECK_BOX(', 23);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_NOTIFY_STOCKADMIN', '0', 'Отправлять уведомления администратору если товар на складе закончился', 'Если опция включена, администратор будет автоматически получать уведомления на e-mail о том, что товар на складе закончился.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_AUTOOFF_STOCKADMIN', '0', 'Отключать товар если он закончился на складе', 'Опция позволяет автоматически отключать товар когда он заканчивается на складе.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_SHOW_COUNTPROD', '0', 'Показывать количество товаров в категориях', 'Включите эту опцию, если вы хотите чтобы в блоках меню и каталоге показывалось число товаров в категориях.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_DISPLAY_PRCODE', '0', 'Отображать код товара на страницах каталога', 'Включите, если вы хотите чтобы на страницах каталога и прайс-листа отображался код(артикул) товара.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_DISP_ORDERNUM', '0', 'Показывать номер заказа при успешном оформлении', 'Опция позволяет покупателям видеть номер их заказа на странице сообщения об успешном оформлении заказа.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (3, 'CONF_DISPLAY_NOPHOTO', '0', 'Выводить картинку-заглушку ("нет фотографии") при отсутствии фотографий у товара', 'Если данная опция включена, при отсутствии у товара фотографий, на страницах каталога будет выводиться стандартная картинка-заглушка "нет фотографии".', 'setting_CHECK_BOX(', 5);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_DISP_INDEXCART', '0', 'Возможность заказа товаров с главной страницы', 'Опция позволяет покупателям делать заказ товаров, которые выводятся на главной странице магазина. Если товар имеет характеристики они не будут учтены при заказе.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_USE_DISCUSS', '1', 'Разрешить покупателям оставлять мнения о товаре', 'Если данная опция включена, покупателям предоставляется возможность оставлять свои комментарии и мнения о товарах .', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_ORDER_ESCAPE', '0', 'Разрешить отмену заказов', 'Опция позволяет покупателям самостоятельно отменять свои заказы, если по каким-либо причинам они от него отказываются. Ссылка для отмены заказа приходит вместе с данными на e-mail.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_SHOW_NULL_STOCK', '0', 'Не показывать товары которых нет на складе', 'Опция запрещает показывать в каталоге те товары, которых нет на складе (если включен учет товаров на складе).', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_USE_RATING', '0', 'Включить систему рейтинга', 'Если данная опция включена, покупателям предоставляется возможность голосовать за товары и сортировать их по уровню популярности.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_SHOW_PARENCAT', '0', 'Выводить список подкатегорий с описаниями', 'Опция полезна, если вы хотите выводить не только товары на странице категории, но и список подкатегорий с их описаниями и фотографиями.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_SECURE_SESSIONS', '0', 'Использовать безопасные сессии', 'При использовании данной опции ip адрес и поле user_agent будут сверяться с начальным значением при старте сессии.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_MAIL_METHOD', '1', 'Способ отправки почты', 'Выберите способ отправки e-mail сообщений. Это может быть серверная функция mail(), либо функция отправки через SMTP протокол(использовать только если mail() не работает).', 'settingCONF_MAIL_METHOD()', 27);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (2, 'CONF_TIMEZONE', '0', 'Часовой пояс (GMT)', 'Если время на вашем сервере не соответствует установленному в вашем часовом поясе, вы можете выбрать один из 24 часовых поясов. Внимание! Выставлять часовой пояс необходимо относительно времени вашего сервера!', 'settingCONF_TIMEZONE()', 27);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (3, 'CONF_DISPLAY_FOTO', '0', 'Угол отображения фотографий товаров', 'Выберите угол отображения фотографий товаров на страницах магазина (справа или слева от описания).', 'settingCONF_DISPLAY_FOTO()', 8);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (3, 'CONF_WATERMARK_IMAGE', '', 'Копирайт-картинка(водяной знак)', 'Укажите изображение находящееся в папке data/, которое будет использоваться в качестве водяного знака. Например: watermark.png', 'setting_TEXT_BOX(0,', 7);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) VALUES (3, 'CONF_PUT_WATERMARK', '0', 'Накладывать на загружаемые фотографии копирайт-картинку(водяной знак)', 'Включите эту опцию если Вы хотите чтобы на загружаемые фотографии автоматически накладывалась копирайт-картинка(водяной знак) указанная в настройках.', 'setting_CHECK_BOX(', 6);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) values (2, 'CONF_MULTYCART', '0', 'Разрешить указывать количество товара для добавления в корзину', 'Если опция включена покупатель может указывать необходимое ему количество товара непосредственно перед добавлением товара в корзину.', 'setting_CHECK_BOX(', 26);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) values (4, 'CONF_AFFILIATE_EMAIL_NEW_PAYMENT', '0', 'Отправлять e-mail покупателю о новом платеже', '', 'setting_CHECK_BOX(', 0);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) values (4, 'CONF_AFFILIATE_PROGRAM_ENABLED', '0', 'Партнерская программа', 'Включить/выключить', 'setting_CHECK_BOX(', 0);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) values (4, 'CONF_AFFILIATE_AMOUNT_PERCENT', '10', 'Процент комиссии', '', 'setting_TEXT_BOX(0,', 0);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) values (4, 'CONF_AFFILIATE_EMAIL_NEW_COMMISSION', '0', 'Отправлять e-mail покупателю о новой комиссии', '', 'setting_CHECK_BOX(', 0);
insert into SETTINGS_TABLE (settings_groupID, settings_constant_name, settings_value, settings_title, settings_description, settings_html_function, sort_order) values (2, 'CONF_ENABLE_REGCONFIRMATION', '0', 'Требовать активацию учетной записи покупателя после регистрации', 'Если данная опция включена, покупателю будет отправляться по электронной почте ключ активации, который он должен ввести после заполнения регистрационной формы. Это позволяет проверять правильность ввода электронного адреса', 'setting_CHECK_BOX(', 26);
